import React from 'react';
import { Link } from 'react-router-dom';

const AppItem = ({app, locationData } ) => {

  const { image, id, name, slug } = app;

  return (
    <div className="app__item" key={id}>
      <div className="app__item__wrap">
      <Link to={{
        pathname: `/app/${slug}`,
        state: locationData? {locationData} : false,
      }}>
        <img src={image} alt={name} />
      </Link>
      </div>
    </div>
  )
}

export default AppItem;
