import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import classNames from 'classnames';
import { fetchCats } from 'actions';
import { getCategories, getActiveCatIdFromState } from 'selectors';

const Categories = props => {
  const { fetchCats, categories, activeCategoryId } = props;

  useEffect(()=>{
    fetchCats()
  },[fetchCats])

  const renderCategory = ({name, id, slug}) => {
    const linkClass = classNames({
      active: id === activeCategoryId
    });
    return (
      <Link to={`/categories/${slug}`} key={id} className={linkClass}>
         {name}
      </Link>
    );
  };
  const renderAllCategory = () => {
    const linkClass = classNames({
      active: !activeCategoryId
    });
    return (
      <Link to='/' className={linkClass}>All</Link>
    );
  };
  return (
    <nav>
      {renderAllCategory()}
      {categories.map(renderCategory)}
    </nav>
  )
}


const mapStateToProps = state => ({
  categories: getCategories(state),
  activeCategoryId: getActiveCatIdFromState(state)
});

const mapDispatchToProps = {
  fetchCats
};

export default connect(mapStateToProps, mapDispatchToProps)(Categories);

