import React from 'react';
import Apps from 'containers/Apps';
import Header from 'components/Header';
import Footer from 'components/Footer';
import ErrorBoundary from 'components/ErrorBoundary';
import SingleApp from 'containers/SingleApp';
import { Switch, Route, Redirect } from 'react-router-dom';


const App = () => {
  return (
    <div id="page-wrap">
    <ErrorBoundary>
      <Route path='/' component={Header} />
        <Switch>        
          <Route exact path='/' render={props=><Apps {...props} type={'main'} page={'appsPage'} />} />
          <Route exact path='/categories/:slug' render={props=><Apps {...props} type={'categorie'} page={'categoriesPage'} />} />
          <Route exact path='/search' render={props=><Apps {...props} type={'search'} page={'searchPage'}/>}/>
          <Route exact path='/app/:slug' render={props=><SingleApp {...props} page={'appPage'}/>} />
          <Redirect to="/" />
        </Switch>
      <Route path='/' component={Footer} />
    </ErrorBoundary>  
    </div>
  )
}

export default App

