import { indexBy, prop, mergeDeepLeft, assoc } from 'ramda';

import {
  GET_APPS_SUCCESS,
  GET_APP_BY_SLUG_SUCCESS,
  GET_SEARCH_SUCCESS,
  GET_CAT_BY_ID_SUCCESS,
  GET_POPULAR_SUCCESS,
} from 'actionTypes';

const indexData = payload => indexBy(prop('id'), payload);

const initialState = {};

export default (state = initialState, {type, payload}) => {
  switch (type) {
    case GET_APPS_SUCCESS:
      return mergeDeepLeft(indexData(payload), state);
    case GET_SEARCH_SUCCESS:
      return mergeDeepLeft(indexData(payload), state);
    case GET_CAT_BY_ID_SUCCESS:
      return mergeDeepLeft(indexData(payload), state);
    case GET_POPULAR_SUCCESS:
      return mergeDeepLeft(indexData(payload), state);
    case GET_APP_BY_SLUG_SUCCESS:
      return assoc(payload.id, payload, state);
    default:
      return state;
  } 
};