import { pluck, mergeRight } from 'ramda';

import {
  GET_APPS_REQUEST,
  GET_APPS_FAILURE,
  GET_APPS_SUCCESS,
  CLEAR_IDS,
} from 'actionTypes';

const initialState = {
  ids: [],
  loading: true,
  error: null
};

export default (state = initialState, {type, payload}) => {
  switch (type) {
    case GET_APPS_SUCCESS:
      const newValues = pluck('id', payload);
      return mergeRight(state, {
        ids: newValues,
        loading: false,
        error: false
      });
    case GET_APPS_REQUEST:
        return mergeRight(state, {
          loading: true,
          error: null,
      })
    case GET_APPS_FAILURE:
        return mergeRight(state, {
          ids: [],
          loading: false,
          error: payload,
      })
    case CLEAR_IDS:
        return mergeRight(state, {
          ids: []
      })
    default:
      return state;
  } 
};