import { indexBy, prop, mergeDeepLeft } from 'ramda';

import {
  GET_CATS_SUCCESS
} from 'actionTypes';

const indexData = payload => indexBy(prop('slug'), payload);

const initialState = {};

export default (state = initialState, {type, payload}) => {
  switch (type) {
    case GET_CATS_SUCCESS:
      return mergeDeepLeft(indexData(payload), state);
    default:
      return state;
  } 
};