import { pluck, mergeRight } from 'ramda';

import {
  GET_SEARCH_REQUEST,
  GET_SEARCH_FAILURE,
  GET_SEARCH_SUCCESS,
  UPDATE_SEARCH_QUERY,
  CLEAR_IDS
} from 'actionTypes';

const initialState = {
  ids: [],
  search: '',
  loading: false,
  error: null,
};

export default (state = initialState, {type, payload}) => {
  switch (type) {
    case GET_SEARCH_SUCCESS:
      const newValues = pluck('id', payload);
      return mergeRight(state, {
        ids: newValues,
        loading: false,
        error: null,
      });
    case GET_SEARCH_REQUEST:
        return mergeRight(state, {
          loading: true,
          error: null,
      })
    case GET_SEARCH_FAILURE:
        return mergeRight(state, {
          ids: [],
          loading: false,
          error: payload,
      })
    case UPDATE_SEARCH_QUERY:
      return mergeRight(state, {
        search: payload
      });
    default:
      return state;
  } 
};