import { prop, map, values, path } from 'ramda';


export const getAppById = (state,id) => prop(id,state.apps);

export const getApps = (state, arrayOfIds) => {
  const apps = map(id => getAppById(state, id), arrayOfIds);
  return apps;
};

export const getCategories = state => values(state.categories);

export const getActiveCatId = (state, match) => {
  const slug = path(['params', 'slug'], match);
  const id = path([`${slug}`, 'id'], state.categories);
  return id;
}

export const getActiveCatIdFromState = state => state.categoriesPage.activeCategory;

export const getPageId = state => state.appPage.id;